import React, { useRef } from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import newuserStyle from "./Newuser.module.css";

export function Newuser() {
  // States for registration
  username = useRef("");
  email = useRef("");
  password = useRef("");
  const navigate = useNavigate();

  // States for checking the errors
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(false);

  // Handling the name change
  const handleName = (e) => {
    username.current=e.target.value
  };

  // Handling the email change
  const handleEmail = (e) => {
    setEmail(e.target.value);
    setSubmitted(false);
  };

  // Handling the password change
  const handlePassword = (e) => {
    setPassword(e.target.value);
    setSubmitted(false);
  };

  // Handling the form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (name === "" || email === "" || password === "") {
      setError(true);
    } else {
      setSubmitted(true);
      setError(false);
    }
  };

  // Showing success message
  const successMessage = () => {
    return (
      <div
        className="success"
        style={{
          display: submitted ? "" : "none",
        }}
      >
        <h1>User {name} successfully registered!!</h1>
      </div>
    );
  };

  // Showing error message if error is true
  const errorMessage = () => {
    return (
      <div
        className="error"
        style={{
          display: error ? "" : "none",
        }}
      >
        <h1>Please enter all the fields</h1>
      </div>
    );
  };

  return (
    <>
      <div className={newuserStyle.inputform}>
        <p>Welcome To The Coffee Shop</p>
        <form onSubmit={handleSubmit}>
          <input
            className={newuserStyle.btn}
            type="text"
            name="Username"
            placeholder="Username"
          />
          <input
            className={newuserStyle.btn}
            type="text"
            name="email"
            placeholder="email"
          />
          <input
            className={newuserStyle.btn}
            type="password"
            name="Password"
            placeholder="Password"
          />

          <input className={newuserStyle.btn} type="submit" value="Submit" />
        </form>
        <div>
          <a onClick={() => navigate("/", { replace: true })}>Existing user</a>
        </div>
      </div>
    </>
  );
}
