import { Navbar } from "./Navbar";
import {
  Table,
  Thead,
  Tbody,
  Tfoot,
  Tr,
  Th,
  Td,
  TableContainer,
  Button,
  IconButton,
  Tooltip,
  VStack,
  FormControl,
  FormLabel,
  Input,
  Textarea,
  Box,
  useToast,
} from "@chakra-ui/react";
import { DeleteIcon } from "@chakra-ui/icons";
import { useRef, useState, memo } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

const Checkout = () => {
  const total = useRef(0);
  const location = useLocation();
  const cartItems = location.state?.cartItems || [];
  const [load, setLoad] = useState(false);
  const [billingDetails, setBillingDetails] = useState({
    name: "",
    address: "",
    city: "",
    state: "",
    zip: "",
    country: "",
    cardNumber: "",
    expirationDate: "",
    cvv: "",
  });
  const toast = useToast();

  const deleteItem = (item) => {
    const data = {
      item: item,
      username: location.state.name,
    };
    try {
      axios
        .delete("http://127.0.0.1:5000/cart", {
          headers: {
            "Content-Type": "application/json",
          },
          data,
        })
        .then((response) => {
          console.log(response.data);
          setLoad(!load);
        });
    } catch (e) {
      console.log("Item cannot be deleted from cart", e);
    }
  };

  const handleBillingChange = (e) => {
    const { name, value } = e.target;
    setBillingDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const orderDetails = {
        items: cartItems,
        billing: billingDetails,
        username: location.state.name,
      };

      const response = await axios.post(
        "http://127.0.0.1:5000/orders",
        orderDetails
      );
      toast({
        title: "Order placed.",
        description: "Your order has been successfully placed!",
        status: "success",
        duration: 5000,
        isClosable: true,
      });
      console.log(response.data);
    } catch (error) {
      console.error("Error placing order:", error);
      toast({
        title: "Error.",
        description: "There was an issue placing your order.",
        status: "error",
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const Billing = memo(() => {
    const username = location.state.name;
    const userCartItems = cartItems.filter(
      (item) => item.username === username
    );

    // Calculate total
    total.current = userCartItems.reduce((acc, item) => {
      const itemTotal = item.data.reduce((itemAcc, product) => {
        return itemAcc + product.price * product.quantity;
      }, 0);
      return acc + itemTotal;
    }, 0);

    return (
      <Box p={5} borderWidth={1} borderRadius="lg" boxShadow="md">
        <TableContainer>
          <Table variant="simple" size="sm">
            <Thead>
              <Tr>
                <Th>Item</Th>
                <Th>Quantity</Th>
                <Th>Price/Unit</Th>
                <Th>Total</Th>
                <Th>Actions</Th>
              </Tr>
            </Thead>
            <Tbody>
              {userCartItems.map((item) =>
                item.data.map((product) => (
                  <Tr key={product.id}>
                    <Td>{product.item}</Td>
                    <Td>{product.quantity}</Td>
                    <Td>${product.price}</Td>
                    <Td>${(product.price * product.quantity).toFixed(2)}</Td>
                    <Td>
                      <Tooltip label="Delete Item">
                        <IconButton
                          onClick={() => deleteItem(product.item)}
                          isRound={true}
                          variant="outline"
                          colorScheme="red"
                          aria-label="Delete"
                          fontSize="20px"
                          icon={<DeleteIcon />}
                        />
                      </Tooltip>
                    </Td>
                  </Tr>
                ))
              )}
            </Tbody>
            <Tfoot>
              <Tr>
                <Th></Th>
                <Th></Th>
                <Th></Th>
                <Th>
                  <h1>
                    <b>Total: ${total.current.toFixed(2)}</b>
                  </h1>
                </Th>
                <Th></Th>
              </Tr>
            </Tfoot>
          </Table>
        </TableContainer>

        {/* Billing Details Form */}
        <VStack as="form" spacing={4} onSubmit={handleSubmit} mt={4}>
          <FormControl isRequired>
            <FormLabel>Name</FormLabel>
            <Input
              type="text"
              name="name"
              value={billingDetails.name}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>Address</FormLabel>
            <Textarea
              name="address"
              value={billingDetails.address}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>City</FormLabel>
            <Input
              type="text"
              name="city"
              value={billingDetails.city}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>State</FormLabel>
            <Input
              type="text"
              name="state"
              value={billingDetails.state}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>ZIP Code</FormLabel>
            <Input
              type="text"
              name="zip"
              value={billingDetails.zip}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>Country</FormLabel>
            <Input
              type="text"
              name="country"
              value={billingDetails.country}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>Card Number</FormLabel>
            <Input
              type="text"
              name="cardNumber"
              value={billingDetails.cardNumber}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
              onFocus={(e) => e.target.select()} // Optional: selects the text on focus
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>Expiration Date (MM/YY)</FormLabel>
            <Input
              type="text"
              name="expirationDate"
              value={billingDetails.expirationDate}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>CVV</FormLabel>
            <Input
              type="text"
              name="cvv"
              value={billingDetails.cvv}
              onChange={handleBillingChange}
              borderWidth={1} // Added border
            />
          </FormControl>
          <Button type="submit" colorScheme="blue">
            Place Order
          </Button>
        </VStack>
      </Box>
    );
  });

  return (
    <>
      <Navbar name={location.state.name} />
      <div>
        <Billing />
      </div>
    </>
  );
};

export default Checkout;
