import { Button, ButtonGroup, useToast, Stack, Textarea } from "@chakra-ui/react";
import { useState } from "react";
import axios from "axios";
import { Navbar } from "./Navbar";
import { useLocation, useNavigate } from "react-router-dom";

export const CustomerService = () => {
  const [description, setDescription] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const toast = useToast();
  const navigate = useNavigate();
  const location = useLocation();

  const handleSubmit = async () => {
    setIsLoading(true);
    const requestData = {
      description, // Send only the description
    };

    try {
      const response = await axios.post("http://localhost:5000/getDecision", requestData);
      toast({
        title: "Success",
        description: "Your request has been processed.",
        status: "success",
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      console.error("Error getting decision:", error);
      toast({
        title: "Error",
        description: "Failed to process your request.",
        status: "error",
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      <Navbar name={location.state.name} />
      <Stack spacing={4} maxWidth="500px" margin="auto" padding="20px">
        <Textarea
          placeholder="Describe your issue here"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <Button colorScheme="blue" onClick={handleSubmit} isLoading={isLoading}>
          Submit
        </Button>
      </Stack>
      <ButtonGroup spacing="2" padding={"48px"}>
        <Button
          variant="solid"
          colorScheme="blue"
          onClick={() => {
            navigate("/orderIssue", {
              state: {
                description: description,
                name: location.state.name,
              },
              replace: true,
            });
          }}
        >
          Report Order Issue
        </Button>
        <Button
          variant="solid"
          colorScheme="blue"
          onClick={() => {
            navigate("/transactionIssue", {
              state: {
                description: description,
                name: location.state.name,
              },
              replace: true,
            });
          }}
        >
          Report Fraudulent Transaction
        </Button>
      </ButtonGroup>
    </>
  );
};

export default CustomerService;
