  //         status: "success",
  //         duration: 5000,
  //         isClosable: true,
  //       });
  //       console.log(response.data);
  //     } catch (error) {
  //       console.error("Error placing order:", error);
  //       toast({
  //         title: "Error.",
  //         description: "There was an issue placing your order.",
  //         status: "error",
  //         duration: 5000,
  //         isClosable: true,
  //       });
  //     }
  //   };
  //   const handleSubmit = async (e) => {
  //     e.preventDefault(); // Prevent default form submission
  //     try {
  //       // Construct order details object
  //       const orderDetails = {
  //         items: cartItems, // Ensure cartItems is correctly formatted
  //         billing: billingDetails, // Ensure billingDetails has all required fields
  //         username: location.state.name, // Ensure this value is present
  //       };

  //       // Send POST request to create the order
  //       const response = await axios.post(
  //         "http://127.0.0.1:5000/orders",
  //         orderDetails
  //       );

  //       // Show success message
  //       toast({
  //         title: "Order placed.",
  //         description: "Your order has been successfully placed!",
  //         status: "success",
  //         duration: 5000,
  //         isClosable: true,
  //       });

  //       console.log(response.data); // Log response data for debugging
  //     } catch (error) {
  //       console.error("Error placing order:", error); // Log error details
  //       toast({
  //         title: "Error.",
  //         description: "There was an issue placing your order.",
  //         status: "error",
  //         duration: 5000,
  //         isClosable: true,
  //       });
  //     }
  //   };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const orderDetails = {
        user_id: userId, // Add userId here
        items: cartItems,
        billing: billingDetails,
        username: location.state.name,
        total_amount: calculateTotalAmount(cartItems), // Ensure you provide the total amount if needed
      };

      const response = await axios.post(
        "http://127.0.0.1:5000/orders",
        orderDetails
      );

      // Success notification...
    } catch (error) {
      // Error handling...
    }
  };
  const Billing = memo(
    ({
      cartItems,
      billingDetails,
      handleBillingChange,
      handleSubmit,
      deleteItem,
    }) => {
      const username = location.state.name;
      const userCartItems = cartItems.filter(
        (item) => item.username === username
      );

      // Calculate total
      total.current = userCartItems.reduce((acc, item) => {
        const itemTotal = item.data.reduce((itemAcc, product) => {
          return itemAcc + product.price * product.quantity;
        }, 0);
        return acc + itemTotal;
      }, 0);

      return (
        <Box p={5} borderWidth={1} borderRadius="lg" boxShadow="md">
          <TableContainer>
            <Table variant="simple" size="sm">
              <Thead>
                <Tr>
                  <Th>Item</Th>
                  <Th>Quantity</Th>
                  <Th>Price/Unit</Th>
                  <Th>Total</Th>
                  <Th>Actions</Th>
                </Tr>
              </Thead>
              <Tbody>
                {userCartItems.map((item) =>
                  item.data.map((product) => (
                    <Tr key={product.id}>
                      <Td>{product.item}</Td>
                      <Td>{product.quantity}</Td>
                      <Td>${product.price}</Td>
                      <Td>${(product.price * product.quantity).toFixed(2)}</Td>
                      <Td>
                        <Tooltip label="Delete Item">
                          <IconButton
                            onClick={() => deleteItem(product.item)}
                            isRound={true}
                            variant="outline"
                            colorScheme="red"
                            aria-label="Delete"
                            fontSize="20px"
                            icon={<DeleteIcon />}
                          />
                        </Tooltip>
                      </Td>
                    </Tr>
                  ))
                )}
              </Tbody>
              <Tfoot>
                <Tr>
                  <Th></Th>
                  <Th></Th>
                  <Th></Th>
                  <Th>
                    <h1>
                      <b>Total: ${total.current.toFixed(2)}</b>
                    </h1>
                  </Th>
                  <Th></Th>
                </Tr>
              </Tfoot>
            </Table>
          </TableContainer>

          {/* Billing Details Form */}
          <VStack as="form" spacing={4} onSubmit={handleSubmit} mt={4}>
            {[
              { label: "Name", name: "name", type: "text" },
              { label: "Address", name: "address", type: "textarea" },
              { label: "City", name: "city", type: "text" },
              { label: "State", name: "state", type: "text" },
              { label: "ZIP Code", name: "zip", type: "text" },
              { label: "Country", name: "country", type: "text" },
              { label: "Card Number", name: "cardNumber", type: "text" },
              {
                label: "Expiration Date (MM/YY)",
                name: "expirationDate",
                type: "text",
              },
              { label: "CVV", name: "cvv", type: "text" },
            ].map(({ label, name, type }) => (
              <FormControl key={name} isRequired>
                <FormLabel>{label}</FormLabel>
                {type === "textarea" ? (
                  <Textarea
                    name={name}
                    value={billingDetails[name]}
                    onChange={handleBillingChange}
                    borderWidth={1}
                  />
                ) : (
                  <Input
                    type={type}
                    name={name}
                    value={billingDetails[name]}
                    onChange={handleBillingChange}
                    borderWidth={1}
                    onFocus={(e) => e.target.select()} // Optional: selects the text on focus
                  />
                )}
              </FormControl>
            ))}
            <Button type="submit" colorScheme="blue">
              Place Order
            </Button>
          </VStack>
        </Box>
      );
    }
  );

  return (
    <>
      <Navbar name={location.state.name} />
      <div>
        <Billing
          cartItems={cartItems}
          billingDetails={billingDetails}
          handleBillingChange={handleBillingChange}
          handleSubmit={handleSubmit}
          deleteItem={deleteItem}
        />
      </div>
    </>
  );
};

export default Checkout;
