import { Navbar } from "./Navbar";
import {
  Table,
  Thead,
  Tbody,
  Tfoot,
  Tr,
  Th,
  Td,
  TableCaption,
  TableContainer,
  Button,
  IconButton,
  Tooltip,
  Card,
  CardHeader,
  CardBody,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Textarea,
  VStack,
  useToast,
} from "@chakra-ui/react";
import { DeleteIcon } from "@chakra-ui/icons";
import { useRef, useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

const Checkout = () => {
  const total = useRef(0);
  total.current = 0;

  const location = useLocation();
  const cartItems = location.state?.cartItems || []; // Get cart items from location state
  const [load, setLoad] = useState(false);
  const [billingDetails, setBillingDetails] = useState({
    name: "",
    address: "",
    city: "",
    state: "",
    zip: "",
    country: "",
  });
  const toast = useToast();

  const deleteItem = (item) => {
    console.log("item=", item);
    const data = {
      item: item,
      username: location.state.name,
    };
    console.log("delete item =", data);
    try {
      axios
        .delete("http://127.0.0.1:5000/cart", {
          headers: {
            "Content-Type": "application/json", // Set the Content-Type header
          },
          data,
        })
        .then((response) => {
          console.log(response.data);
          setLoad(!load);
        });
    } catch (e) {
      console.log("Item cannot be deleted from cart", e);
    }
  };

  const handleBillingChange = (e) => {
    const { name, value } = e.target;
    setBillingDetails((prevDetails) => ({ ...prevDetails, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const orderDetails = {
        items: cartItems,
        billing: billingDetails,
        username: location.state.name,
      };

      const response = await axios.post(
        "http://127.0.0.1:5000/orders",
        orderDetails
      );
      toast({
        title: "Order placed.",
        description: "Your order has been successfully placed!",
        status: "success",
        duration: 5000,
        isClosable: true,
      });
      console.log(response.data);
    } catch (error) {
      console.error("Error placing order:", error);
      toast({
        title: "Error.",
        description: "There was an issue placing your order.",
        status: "error",
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const Cart = () => {
    const username = location.state.name;
    const userCartItems = cartItems.filter(
      (item) => item.username === username
    );

    return (
      <>
        <TableContainer>
          <Table variant="simple" size="sm">
            {/* <TableCaption>
              <Button colorScheme="blue" onClick={handleSubmit}>
                Proceed To Checkout
              </Button>
            </TableCaption> */}
            <Thead>
              <Tr>
                <Th>Item</Th>
                <Th>Quantity</Th>
                <Th>Price/Unit</Th>
                <Th>Total</Th>
                <Th>Actions</Th>
              </Tr>
            </Thead>
            <Tbody>
              {userCartItems.map((item) => {
                const { data } = item;
                total.current = 0;
                {Cons}
                data.forEach((product) => {
                  total.current += product.price * product.quantity;
                });

                return (
                  <Tr key={item.id}>
                    {data.map((product) => (
                      <Tr key={product.id}>
                        <Td>{product.item}</Td>
                        <Td>{product.quantity}</Td>
                        <Td>${product.price}</Td>
                        <Td>
                          ${(product.price * product.quantity).toFixed(2)}
                        </Td>
                        <Td>
                          <Tooltip label="Delete Item">
                            <IconButton
                              onClick={() => deleteItem(product.item)}
                              isRound={true}
                              variant="outline"
                              colorScheme="red"
                              aria-label="Delete"
                              fontSize="20px"
                              icon={<DeleteIcon />}
                            />
                          </Tooltip>
                        </Td>
                      </Tr>
                    ))}
                  </Tr>
                );
              })}
            </Tbody>
            <Tfoot>
              <Tr>
                <Th></Th>
                <Th></Th>
                <Th></Th>
                <Th>
                  <h1>
                    <b>Total : ${total.current.toFixed(2)}</b>
                  </h1>
                </Th>
                <Th></Th>
              </Tr>
            </Tfoot>
          </Table>
        </TableContainer>

        {/* Billing Details Form */}
        <VStack as="form" spacing={4} onSubmit={handleSubmit} mt={4}>
          <FormControl isRequired>
            <FormLabel>Name</FormLabel>
            <Input
              type="text"
              name="name"
              value={billingDetails.name}
              onChange={handleBillingChange}
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>Address</FormLabel>
            <Textarea
              name="address"
              value={billingDetails.address}
              onChange={handleBillingChange}
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>City</FormLabel>
            <Input
              type="text"
              name="city"
              value={billingDetails.city}
              onChange={handleBillingChange}
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>State</FormLabel>
            <Input
              type="text"
              name="state"
              value={billingDetails.state}
              onChange={handleBillingChange}
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>ZIP Code</FormLabel>
            <Input
              type="text"
              name="zip"
              value={billingDetails.zip}
              onChange={handleBillingChange}
            />
          </FormControl>
          <FormControl isRequired>
            <FormLabel>Country</FormLabel>
            <Input
              type="text"
              name="country"
              value={billingDetails.country}
              onChange={handleBillingChange}
            />
          </FormControl>
          <Button type="submit" colorScheme="blue">
            Place Order
          </Button>
        </VStack>
      </>
    );
  };

  return (
    <>
      <Navbar name={location.state.name} />
      <div>
        <Cart />
      </div>
    </>
  );
};

export default Checkout;
