import {
  Button,
  Input,
  InputGroup,
  InputLeftAddon,
  Stack,
} from "@chakra-ui/react";
import axios from "axios";
import { useState } from "react";
import StoresStyle from "./Stores.module.css";
import { Navbar } from "./Navbar";
import { useLocation } from "react-router-dom";

export function Stores({ setload }) {
  const location = useLocation();
  const [locationName, setLocationName] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [zipcode, setZipcode] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const addLocation = async () => {
    const date = new Date().toISOString().split("T")[0]; // Format YYYY-MM-DD

    const locationData = {
      locationName,
      address,
      city,
      zipcode,
      createdDate: date,
      addedBy: "Admin",
    };

    try {
      setIsLoading(true);
      const response = await axios.post(
        "http://127.0.0.1:5000/addLocation",
        locationData
      );
      console.log("Location added:", response.data);
      setload(true);
      setIsLoading(false);
    } catch (e) {
      console.log("New location cannot be added", e);
      setIsLoading(false);
      alert("Error: Location could not be added.");
    }
  };

  return (
    <>
        <div className={dashboardStyle.content}>
      <Navbar name={location.state?.name || "Guest"} />
      <div className={StoresStyle.locationinput}>
        <Stack spacing={4}>
          <InputGroup>
            <InputLeftAddon children="Location Name" />
            <Input
              type="text"
              placeholder="Enter location name"
              value={locationName}
              onChange={(e) => setLocationName(e.target.value)}
            />
          </InputGroup>

          <InputGroup>
            <InputLeftAddon children="Address" />
            <Input
              type="text"
              placeholder="Enter address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </InputGroup>

          <InputGroup>
            <InputLeftAddon children="City" />
            <Input
              type="text"
              placeholder="Enter city"
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
          </InputGroup>

          <InputGroup>
            <InputLeftAddon children="Zipcode" />
            <Input
              type="text"
              placeholder="Enter zipcode"
              value={zipcode}
              onChange={(e) => setZipcode(e.target.value)}
            />
          </InputGroup>

          <Button
            colorScheme="teal"
            onClick={addLocation}
            isLoading={isLoading}
            isDisabled={!locationName || !address || !city || !zipcode}
          >
            Add Location
          </Button>
        </Stack>
      </div>
      </div>
    </>
  );
}
