import React, { useReducer, useRef } from "react";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import newuserStyle from "./Newuser.module.css";
import axios from "axios";

export function Newuser() {
  // States for registration
  const username = useRef("");
  const email = useRef("");
  const password = useRef("");
  const navigate = useNavigate();
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(false);
  const [userexist, setuserexist] = useState(false);
  const [emailexist, setemailexist] = useState(false);
  const [loggedin, setloggedin] = useState(false);
  const [state,dispatch]=useReducer(reducer,{
    error:false,
    submitted:false,
    userexist:false,
    emailexist:false,
    loggedin:false

  })
  let users = null;
  useEffect(() => {
    try {
      axios.get(`http://127.0.0.1:5000/login`).then((res) => {
        console.log("result=", res);
        users = res;
        console.log("users", users);
      });
    } catch (e) {
      console.log(e);
    }
  }, [loggedin]);

  const handleSubmit = (e) => {
    console.log("in submit", e);
    setSubmitted(false);
    setError(false);
    setloggedin(true);
    e.preventDefault();
    if (
      username.current.value === "" ||
      email.current.value === "" ||
      password.current.value === ""
    ) {
      setError(true);
    } 
    else {
      const account = users.data.find(
        (user) => user.username === username.current.value
      );

      setSubmitted(true);
      setError(false);
    }
  };

  const Successmessage = () => {
    return (
      <div
        className="success"
        style={{
          display: submitted ? "" : "none",
        }}
      >
        <h1>User {username} successfully registered!!</h1>
      </div>
    );
  };

  const Errormessage = () => {
    return (
      <div
        className="error"
        style={{
          display: error ? "" : "none",
        }}
      >
        <h1>Please enter all the fields</h1>
      </div>
    );
  };
  const Existingusername = () => {
    return (
      <div
        className="error"
        style={{
          display: error ? "" : "none",
        }}
      >
        <h1>Username alredy exist</h1>
      </div>
    );
  };

  return (
    <>
      <div className={newuserStyle.inputform}>
        <p>Welcome To The Coffee Shop</p>
        <form onSubmit={handleSubmit}>
          <input
            className={newuserStyle.btn}
            type="text"
            name="Username"
            ref={username}
            placeholder="Username"
          />
          <input
            className={newuserStyle.btn}
            type="text"
            name="email"
            ref={email}
            placeholder="email"
          />
          <input
            className={newuserStyle.btn}
            type="password"
            name="Password"
            ref={password}
            placeholder="Password"
          />

          <input className={newuserStyle.btn} type="submit" value="Submit" />
        </form>
        <div className={newuserStyle.invalid}>
          {error && <Errormessage />}
          {submitted && <Successmessage />}
        </div>
        <div>
          <a onClick={() => navigate("/", { replace: true })}>Existing user</a>
        </div>
      </div>
    </>
  );
}
